/*	Discrete buttons are those that turn things on or off at each press.  They differ from momentary buttons which
 *	only keep something on or off while they are held down.
 */



task main()
{
	initializeRobot();

	waitForStart();   // wait for start of tele-op phase

	while (true)
	{
		Controller primary, secondary;
		UpdatePrimaryController(primary);
		UpdateSecondaryController(secondary);


		nxtDisplayTextLine(0, (string)primary.leftJoystick.y);


		SetBoostState(ControllerButtonIsToggledOn(primary, ControllerButton2));  //  set boost mode

		nxtDisplayTextLine(0, (string)ControllerButtonIsToggledOn(primary, ControllerButton2));


		//	Drive
		OmniArcadeDrive(primary.rightJoystick, primary.leftJoystick);	//	right = arcade, left = sidewind
		//OmniStrafeDrive(primary.leftJoystick, primary.rightJoystick);	//	left = strafe, right = turn




		//	FIXME: do mechanism stuff


		//	FIXME: implement
	}
}
